  <script>
  (function() {
    const conversionFactors = window.conversionFactors || (typeof conversionFactors !== 'undefined' ? conversionFactors : {});
    const fromInput = document.getElementById('fromValue');
    const toInput = document.getElementById('toValue');
    const fromSelect = document.getElementById('fromUnit');
    const toSelect = document.getElementById('toUnit');
    const units = ["aln", "angstrom", "arpent", "astronomical-unit", "attometer", "barleycorn", "bohr-radius", "break", "caliber", "centiinch", "centimeter", "chain", "chain-us-survey", "cubit-greek", "cubit-uk", "decimeter", "dekameter", "earths-distance-from", "earths-equatorial-radius", "earths-polar-radius", "electron-radius-classical", "ell", "exameter", "famn", "fathom", "fathom-us-survey", "femtometer", "fermi", "finger-cloth", "fingerbreadth", "foot", "foot-us-survey", "furlong", "furlong-us-survey", "gigameter", "hand", "handbreadth", "hectometer", "inch", "inch-us-survey", "ken", "kilometer", "kiloparsec", "kiloyard", "league", "league-statute", "light-year", "link", "link-us-survey", "long-cubit", "long-reed", "megameter", "megaparsec", "meter", "microinch", "micrometer", "micron", "mil", "mile", "mile-roman", "mile-statute", "mile-us-survey", "millimeter", "nail-cloth", "nanometer", "nautical-league-int.", "nautical-league-uk", "nautical-mile-international", "nautical-mile-uk", "parsec", "perch", "petameter", "pica", "picometer", "planck-length", "point", "pole", "reed", "rod", "rod-us-survey", "roman-actus", "rope", "russian-archin", "span-cloth", "terameter", "twip", "vara-castellana", "vara-conuquera", "vara-de-tarea", "x-unit", "yard"];

    // Populate dropdowns
    function label(u) { return u.replace(/-/g,' '); }
    units.forEach(u => {
      const o1 = document.createElement('option'); o1.value = u; o1.textContent = label(u);
      const o2 = document.createElement('option'); o2.value = u; o2.textContent = label(u);
      fromSelect.appendChild(o1);
      toSelect.appendChild(o2);
    });
    // Default common pair
    const defaultFrom = 'meter';
    const defaultTo = units.includes('foot') ? 'foot' : (units[1] || 'kilometer');
    fromSelect.value = defaultFrom;
    toSelect.value = defaultTo;

    // Helper: get factor between any two units via meter
    function factorAtoB(a, b) {
      if (a === b) return 1;
      let direct = conversionFactors[a + '-to-' + b];
      if (direct != null) return direct;
      // try inverse direct
      const inv = conversionFactors[b + '-to-' + a];
      if (inv != null) return 1 / inv;
      // route via meter if possible
      function toMeter(x) {
        if (x === 'meter') return 1;
        let xm = conversionFactors[x + '-to-meter'];
        if (xm != null) return xm;
        let mx = conversionFactors['meter-to-' + x];
        if (mx != null) return 1 / mx;
        return null;
      }
      const a2m = toMeter(a);
      const b2m = toMeter(b);
      if (a2m != null && b2m != null) return a2m / b2m;
      return null;
    }

    function convert() {
      const a = fromSelect.value;
      const b = toSelect.value;
      const v = parseFloat(fromInput.value || '0');
      const k = factorAtoB(a, b);
      const out = (k != null) ? v * k : NaN;
      toInput.value = Number.isFinite(out) ? String(out) : 'â€”';
    }

    fromInput.addEventListener('input', convert);
    fromSelect.addEventListener('change', convert);
    toSelect.addEventListener('change', convert);
    document.getElementById('swapBtn').addEventListener('click', function() {
      const a = fromSelect.value;
      fromSelect.value = toSelect.value;
      toSelect.value = a;
      convert();
    });

    // Build "Complete list of length units for conversion"
    const container = document.getElementById('lengthUnits');
    const ul = document.createElement('ul');
    units.forEach(u => {
      if (u === 'meter') return;
      const f = factorAtoB(u, 'meter');
      const li = document.createElement('li');
      if (f != null) {
        li.innerHTML = `1 <strong>${label(u)}</strong> = <code>${f}</code> meter [m]`;
      } else {
        li.innerHTML = `<strong>${label(u)}</strong> &mdash; factor unavailable`;
      }
      ul.appendChild(li);
    });
    container.appendChild(ul);

    // Initial calculation
    convert();
  })();
  </script>
